const Industry = () => {
  const [activeSection, setActiveSection] = useState(sections[0].id);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const currentSection = sections.find((section) => section.id === activeSection);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <>
    <I.MenuToggle onClick={toggleMenu}>
      <img
        src={isMenuOpen ? Close : Open}
        alt="Menu Toggle"
        className="menu-icon"
      />
    </I.MenuToggle>

      <I.Div></I.Div>
      <I.Container>
      <I.Sidebar $isMenuOpen={isMenuOpen}>
      <I.SidebarStickyContainer>
          {sections.map((section) => (
            <I.SidebarItem 
              key={section.id}
              $isActive={activeSection === section.id}
              onClick={() => setActiveSection(section.id)}
            >
              {section.title}
            </I.SidebarItem>
          ))}</I.SidebarStickyContainer>
        </I.Sidebar>

        <I.Content>
          <I.Title>{currentSection.title}</I.Title>
          <I.Description>{currentSection.content}</I.Description>

          {currentSection.subsections && (
            <I.SubsectionContainer>
              {currentSection.subsections.map((subsection) => (
                <div key={subsection.id}>
                  <I.SubsectionTitle>{subsection.title}</I.SubsectionTitle>
                  <I.SubsectionContent>{subsection.content}</I.SubsectionContent>
                  {subsection.content2 && <I.SubsectionContent>{subsection.content2}</I.SubsectionContent>}
                  {subsection.content3 && <I.SubsectionContent>{subsection.content3}</I.SubsectionContent>}
                  {subsection.content4 && <I.SubsectionContent>{subsection.content4}</I.SubsectionContent>}
                  {subsection.content5 && <I.SubsectionContent>{subsection.content5}</I.SubsectionContent>}
                  {subsection.content6 && <I.SubsectionContent>{subsection.content6}</I.SubsectionContent>}
                  {subsection.content7 && <I.SubsectionContent>{subsection.content7}</I.SubsectionContent>}
                </div>
              ))}
            </I.SubsectionContainer>
          )}
        </I.Content>
      </I.Container>
    </>
  );
};

export default Industry;
